function createDots(){slides.forEach((e,t)=>{let n=document.createElement("a");n.href="#",n.classList.add("dot"),n.addEventListener("click",()=>{changeSlide(t)}),dotsContainer.appendChild(n)})}function changeSlide(e){currentIndex=e,updateCarouselContent()}function updateCarouselContent(){slides.forEach((e,t)=>{e.classList.toggle("active",t===currentIndex),e.style.display=t===currentIndex?"block":"none"});const e=document.querySelectorAll(".dot");e.forEach((e,t)=>{e.classList.toggle("dot_selected",t===currentIndex)})}const imgBanner=document.querySelector(".banner__all-img"),chevronLeft=document.querySelector(".chevron_left"),chevronRight=document.querySelector(".chevron_right"),dotsContainer=document.querySelector(".dots"),slides=imgBanner.querySelectorAll("img");let currentIndex=0;chevronRight.addEventListener("click",()=>{changeSlide((currentIndex+1)%slides.length)}),chevronLeft.addEventListener("click",()=>{changeSlide((currentIndex-1+slides.length)%slides.length)}),createDots(),updateCarouselContent();